public void printValues(int start, int end) {
    int[] array = getArray(start, end);

    for(int i = 0; i < array.length; i++) {
        System.out.println((array));
    }
}

private int[] getArray(int start, int end) {
    int[] array = new int[end - start + 1];
    for(int i = 0; i <= end - start; i++) {
        array[i] = start + i;
    }
    return array;
}

public int[] getArray(int start, int end) {
    int[] array = new int[end - start + 1];
    for(int i = 0; i <= end - start; i++) {
        array[i] = start + i;
    }
    return array;
}

private void countToTen(int a, int b) {
    for(int i = 1; i <= 10; i++) {
        if(i == a) {
            break;      
        }
        if(i == b) {
            continue;
        }
        System.out.println(i);
    }
}

private void continueOnEven() {
    System.out.println("Counting from 1 to 10, skipping even numbers.");
    for (int i = 0; i < 10; i++){
        if(i % 2 == 0) {
            continue;
        }
        System.out.println(i);
       }
}
